sys_script_client.script:
function onLoad() {
    if (g_scratchpad.hasAttachments) {
        alert("Recevied scratchpad object. Displaying the css property:" + g_scratchpad.css);
    } else {
        alert("The scratchpad object does not have the css property.");
    }

    if (g_scratchpad.managerName) {
        alert("Recevied scratchpad object. Displaying the user name who created this incident:" + g_scratchpad.managerName);
    } else {
        alert("The scratchpad object does not have the managerName property.");
    }
}
